mapper.select:
select 

from cj_ods_data_script_def_info
where business_system_name_short_name = #{businessSystemNameShortName,jdbcType=VARCHAR}
  and data_source_schema = #{dataSourceSchema,jdbcType=VARCHAR}
  and data_source_table = #{dataSourceTable,jdbcType=VARCHAR}
  and ods_data_table = #{odsDataTable,jdbcType=VARCHAR}

mapper.delete:
delete from cj_ods_data_script_def_info
where business_system_name_short_name = #{businessSystemNameShortName,jdbcType=VARCHAR}
  and data_source_schema = #{dataSourceSchema,jdbcType=VARCHAR}
  and data_source_table = #{dataSourceTable,jdbcType=VARCHAR}
  and ods_data_table = #{odsDataTable,jdbcType=VARCHAR}

mapper.insert:
insert into cj_ods_data_script_def_info (business_system_name_short_name, data_source_schema, 
  data_source_table, ods_data_table, business_system_ID, 
  ods_data_script_type, ods_data_sqoop_define, 
  ods_data_hivesql_define, last_modify_dt, last_modify_by
  )
values (#{businessSystemNameShortName,jdbcType=VARCHAR}, #{dataSourceSchema,jdbcType=VARCHAR}, 
  #{dataSourceTable,jdbcType=VARCHAR}, #{odsDataTable,jdbcType=VARCHAR}, #{businessSystemId,jdbcType=VARCHAR}, 
  #{odsDataScriptType,jdbcType=VARCHAR}, #{odsDataSqoopDefine,jdbcType=VARCHAR}, 
  #{odsDataHivesqlDefine,jdbcType=VARCHAR}, #{lastModifyDt,jdbcType=VARCHAR}, #{lastModifyBy,jdbcType=VARCHAR}
  )

mapper.update:
update cj_ods_data_script_def_info


